letions 4, :changes 5, :sha "4667ef54da36bc2f8eceb077fd61bebc9950a843"} {:additions 2, :raw_url "https://github.com/rails/jbuilder/raw/003a369f7432c7173d00c453484ae11f1c952a15/lib/generators/rails/templates/partial.json.jbuilder", :contents_url "https://api.github.com/repos/rails/jbuilder/contents/lib/generators/rails/templates/partial.json.jbuilder?ref=003a369f7432c7173d00c453484ae11f1c952a15", :patch "@@ -0,0 +1,2 @@\n+json.extract! <%= singular_table_name %>, <%= attributes_list_with_timestamps %>\n+json.url <%= singular_table_name %>_url(<%= singular_table_name %>, format: :json)\n\\ No newline at end of file", :blob_url "https://github.com/rails/jbuilder/blob/003a369f7432c7173d00c453484ae11f1c952a15/lib/generators/rails/templates/partial.json.jbuilder", :filename "lib/generators/rails/templates/partial.json.jbuilder", :status "added", :deletions 0, :changes 2, :sha "6398456b887257e222249751865b7ffcb6a4f42f"} {:additions 1, :raw_url "https://github.com/rails/jbuilder/raw/003a369f7432c7173d00c453484ae11f1c952a15/lib/generators/rails/templates/show.json.jbuilder", :contents_url "https://api.github.com/repos/rails/jbuilder/contents/lib/generators/rails/templates/show.json.jbuilder?ref=003a369f7432c7173d00c453484ae11f1c952a15", :patch "@@ -1 +1 @@\n-json.extract! @<%= singular_table_name %>, <%= attributes_list_with_timestamps %>\n+json.partial! \"<%= plural_table_name %>/<%= singular_table_name %>\", <%= singular_table_name %>: @<%= singular_table_name %>\n\\ No newline at end of file", :blob_url "https://github.com/rails/jbuilder/blob/003a369f7432c7173d00c453484ae11f1c952a15/lib/generators/rails/templates/show.json.jbuilder", :filename "lib/generators/rails/templates/show.json.jbuilder", :status "modified", :deletions 1, :changes 2, :sha "683a29c1182f2a23c9d11a0103ae6281a724d7ae"} {:additions 29, :raw_url "https://github.com/rails/jbuilder/raw/003a369f7432c7173d00c453484ae11f1c952a15/lib/jbuilder/jbuilder_template.rb", :contents_url "https://api.github.com/repos/rails/jbuilder/contents/lib/jbuilder/jbuilder_template.rb?ref=003a369f7432c7173d00c453484ae11f1c952a15", :patch "@@ -32,7 +32,7 @@ def partial!(*args)\n   #   end\n   def cache!(key=nil, options={})\n     if @context.controller.perform_caching\n-      value = ::Rails.cache.fetch(_cache_key(key, options), options) do\n+      value = _cache_fragment_for(key, options) do\n         _scope { yield self }\n       end\n \n@@ -102,9 +102,35 @@ def _render_partial(options)\n     @context.render options\n   end\n \n+  def _cache_fragment_for(key, options, &block)\n+    key = _cache_key(key, options)\n+    _read_fragment_cache(key, options) || _write_fragment_cache(key, options, &block)\n+  end\n+\n+  def _read_fragment_cache(key, options = nil)\n+    @context.controller.instrument_fragment_cache :read_fragment, key do\n+      ::Rails.cache.read(key, options)\n+    end\n+  end\n+\n+  def _write_fragment_cache(key, options = nil)\n+    @context.controller.instrument_fragment_cache :write_fragment, key do\n+      yield.tap do |value|\n+        ::Rails.cache.write(key, value, options)\n+      end\n+    end\n+  end\n+\n   def _cache_key(key, options)\n-    key = _fragment_name_with_digest(key, options)\n-    key = url_for(key).split('://', 2).last if ::Hash === key\n+    name_options = options.slice(:skip_digest, :virtual_path)\n+    key = _fragment_name_with_digest(key, name_options)\n+\n+    if @context.respond_to?(:fragment_cache_key)\n+      key = @context.fragment_cache_key(key)\n+    else\n+      key = url_for(key).split('://', 2).last if ::Hash === key\n+    end\n+\n     ::ActiveSupport::Cache.expand_cache_key(key, :jbuilder)\n   end\n ", :blob_url "https://github.com/rails/jbuilder/blob/003a369f7432c7173d00c453484ae11f1c952a15/lib/jbuilder/jbuilder_template.rb", :filename "lib/jbuilder/jbuilder_template.rb", :status "modified", :deletions 3, :changes 32, :sha "882c0305836e4e5d3b6424e59e2ed9c8da78aeff"} {:additions 1, :raw_url "https://github.com/rails/jbuilder/raw/003a369f7432c7173d00c453484ae11f1c952a15/lib/jbuilder/railtie.rb", :contents_url "https://api.github.com/repos/rails/jbuilder/contents/lib/jbuilder/railtie.rb?ref=003a369f7432c7173d00c453484ae11f1c952a15", :patch "@@ -3,7 +3,7 @@\n \n class Jbuilder\n   class Railtie < ::Rails::Railtie\n-    initializer :jbuilder do |app|\n+    initializer :jbuilder do\n       ActiveSupport.on_load :action_view do\n         ActionView::Template.register_template_handler :jbuilder, JbuilderHandler\n         require 'jbuilder/dependency_tracker'", :blob_url "https://github.com/rails/jbuilder/blob/003a369f7432c7173d00c453484ae11f1c952a15/lib/jbuilder/railtie.rb", :filename "lib/jbuilder/railtie.rb", :status "modified", :deletions 1, :changes 2, :sha "ebb4a5f7ba839198aa8f70857443676573b26f6b"} {:additions 10, :raw_url "https://github.com/rails/jbuilder/raw/003a369f7432c7173d00c453484ae11f1c952a15/test/jbuilder_generator_test.rb", :contents_url "https://api.github.com/repos/rails/jbuilder/contents/test/jbuilder_generator_test.rb?ref=003a369f7432c7173d00c453484ae11f1c952a15", :patch "@@ -14,19 +14,25 @@ class JbuilderGeneratorTest < Rails::Generators::TestCase\n     %w(index show).each do |view|\n       assert_file \"app/views/posts/#{view}.json.jbuilder\"\n     end\n+    assert_file \"app/views/posts/_post.json.jbuilder\"\n   end\n \n   test 'index content' do\n     run_generator\n \n     assert_file 'app/views/posts/index.json.jbuilder' do |content|\n-      assert_match /json\\.array!\\(@posts\\) do \\|post\\|/, content\n-      assert_match /json\\.extract! post, :id, :title, :body/, content\n-      assert_match /json\\.url post_url\\(post, format: :json\\)/, content\n+      assert_match /json.array! @posts, partial: 'posts\\/post', as: :post/, content\n     end\n \n     assert_file 'app/views/posts/show.json.jbuilder' do |content|\n-      assert_match /json\\.extract! @post, :id, :title, :body, :created_at, :updated_at/, content\n+      assert_match /json.partial! \\\"posts\\/post\\\", post: @post/, content\n+    end\n+    \n+    assert_file 'app/views/posts/_post.json.jbuilder' do |content|            \n+      assert_match /json\\.extract! post, :id, :title, :body/, content\n+      assert_match /json\\.url post_url\\(post, format: :json\\)/, content\n     end\n+    \n+\n   end\n end", :blob_url "https://github.com/rails/jbuilder/blob/003a369f7432c7173d00c453484ae11f1c952a15/test/jbuilder_generator_test.rb", :filename "test/jbuilder_generator_test.rb", :status "modified", :deletions 4, :changes 14, :sha "7e552b9ab2a7dfdef99e97526b14ade94a53aff5"} {:additions 41, :raw_url "https://github.com/rails/jbuilder/raw/003a369f7432c7173d00c453484ae11f1c952a15/test/jbuilder_template_test.rb", :contents_url "https://api.github.com/repos/rails/jbuilder/contents/test/jbuilder_template_test.rb?ref=003a369f7432c7173d00c453484ae11f1c952a15", :patch "@@ -332,6 +332,35 @@ def assert_collection_rendered(result, context = nil)\n     JBUILDER\n   end\n \n+  test \"fragment caching uses fragment_cache_key\" do\n+    undef_context_methods :fragment_name_with_digest, :cache_fragment_name\n+\n+    @context.expects(:fragment_cache_key).with(\"cachekey\")\n+\n+    jbuild <<-JBUILDER\n+      json.cache! \"cachekey\" do\n+        json.name \"Cache\"\n+      end\n+    JBUILDER\n+  end\n+\n+  test \"fragment caching instrumentation\" do\n+    undef_context_methods :fragment_name_with_digest, :cache_fragment_name\n+\n+    payloads = {}\n+    ActiveSupport::Notifications.subscribe(\"read_fragment.action_controller\") { |*args| payloads[:read_fragment] = args.last }\n+    ActiveSupport::Notifications.subscribe(\"write_fragment.action_controller\") { |*args| payloads[:write_fragment] = args.last }\n+\n+    jbuild <<-JBUILDER\n+      json.cache! \"cachekey\" do\n+        json.name \"Cache\"\n+      end\n+    JBUILDER\n+\n+    assert_equal \"jbuilder/cachekey\", payloads[:read_fragment][:key]\n+    assert_equal \"jbuilder/cachekey\", payloads[:write_fragment][:key]\n+  end\n+\n   test \"current cache digest option accepts options\" do\n     undef_context_methods :fragment_name_with_digest\n \n@@ -345,6 +374,18 @@ def assert_collection_rendered(result, context = nil)\n     JBUILDER\n   end\n \n+  test \"fragment caching accepts expires_in option\" do\n+    undef_context_methods :fragment_name_with_digest\n+\n+    @context.expects(:cache_fragment_name).with(\"cachekey\", {})\n+\n+    jbuild <<-JBUILDER\n+      json.cache! \"cachekey\", expires_in: 1.minute do\n+        json.name \"Cache\"\n+      end\n+    JBUILDER\n+  end\n+\n   test \"does not perform caching when controller.perform_caching is false\" do\n     controller.perform_caching = false\n ", :blob_url "https://github.com/rails/jbuilder/blob/003a369f7432c7173d00c453484ae11f1c952a15/test/jbuilder_template_test.rb", :filename "test/jbuilder_template_test.rb", :status "modified", :deletions 0, :changes 41, :sha "e30288192f61572e0fc185b0048c0e59e93559a8"}], :total_commits 14}}]}
